import React, { useState } from "react";
import Icon from "react-native-vector-icons/AntDesign" 
import { SafeAreaView, View, Text, ScrollView, StyleSheet, TouchableOpacity, TextInput } from "react-native";
import { 
    FirstView1, 
    BigText1,
    SecondView1,
    PasteText

} from "../componenets/fintech2Style";
import AsyncStorage from '@react-native-async-storage/async-storage';

let STORAGE_KEY = '@user_input';

const saveData = async () => {
    try {
      await AsyncStorage.setItem(STORAGE_KEY, age)
      alert('Data successfully saved')
    } catch (e) {
      alert('Failed to save the data to the storage')
    }
  }

  const readData = async () => {
    try {
      const value = await AsyncStorage.getItem(STORAGE_KEY);
  
      if (value !== null) {
        setLink(value);
      }
    } catch (e) {
      alert('Failed to fetch the input from storage');
    }
  };

//   useEffect(() => {
//     readData();
//   }, []);

const onChangeText = value => setLink(value);

const onSubmitEditing = () => {
  if (!link) return;

  saveData(link);
  setLink('');
};



export default function Fintech2({navigation}){
    const [link, setLink] = useState("");
    return(
        <SafeAreaView style={styles.fintech2container}>
            <ScrollView>
                <FirstView1>
                    <TouchableOpacity style={styles.back} onPress={()=>navigation.navigate("Fintech1")}>
                   <Icon size={30} color="#C3AD60" name="back" style={styles.pad1}/> 
                    </TouchableOpacity>
                   <BigText1 style={styles.pad}>Procurement</BigText1>
                </FirstView1>
                <SecondView1>
                    <TextInput 
                    style={styles.input}
                    placeholderTextColor="#fff"
                    value={link}
                    placeholder="Enter"
                    onChangeText={onChangeText}
                    onSubmitEditing={onSubmitEditing}
                    />
                    <TouchableOpacity onPress={()=>navigation.navigate("Fintech5")} style={styles.paste}><PasteText>Paste</PasteText></TouchableOpacity>
                </SecondView1>
            </ScrollView>
        </SafeAreaView>
    )
}
//#CDA360
const styles = StyleSheet.create({
    fintech2container: {
        backgroundColor: "#000",
        paddingTop: 25,
        flex: 1,
    },
    pad: {
        paddingLeft: 59,
    },
    pad1: {
        paddingLeft: 30
    },
    input: {
        backgroundColor: "#4E4E4E",
        width: 320,
        height: 48,
        borderRadius: 100,
        flexDirection: "row",
        paddingLeft: 20,
        color: "#fff"   
    },
    paste: {
        backgroundColor: "#C3AD60",
        height: 48,
        width: 180,
        borderRadius: 100,
        alignItems: "center",
        justifyContent: "center",
        color: "#000",
        marginTop: 48,
    },
    back: {
        padding: 10,
    }

})
