import React from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity} from 'react-native';
import { RadioButton } from 'react-native-paper';
import Icon2  from 'react-native-vector-icons/AntDesign';
import styled from 'styled-components/native';


const StyledCard1= styled.Image`
width: 85.33px;
height: 48px;
border: 1px solid #000000;
filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
`;

const CardsView= styled.View`
flex-direction: row;
align-items: center;
justify-content: space-around;
`;

const CardView= styled.View`
margin: 40px 0 40px 0;
`;

const Head= styled.Text`

font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 20px;
margin-bottom:20px;
color: #C3AD60;

border: 1px solid #000000;
text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;

const StyledInput= styled.TextInput`
height: 48px;
width:100%;
background: #4E4E4E;
border-radius: 5px;
`;



const StyledText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 14px;
color: #FFFFFF;
`; 

const DateBtn= styled.TouchableOpacity`
width: 96px;
height: 48px;
align-items:center;
justify-content:center;
background-color: #C3AD60;
border-radius: 10px;
`;

const StyledDateView= styled.TextInput`
width: 96px;
height: 48px;
background: #4E4E4E;
border-radius: 5px;
`;




export default function Ecommerce8({navigation}){
	return(
		<SafeAreaView style={styles.container}>
		<CardView>
			<Head>Saved Cards</Head>
			<CardsView>
				<TouchableOpacity><StyledCard1 source={require('../assets/card1.png')}/></TouchableOpacity>
				<TouchableOpacity><StyledCard1 source={require('../assets/card2.png')}/></TouchableOpacity>
				<TouchableOpacity><StyledCard1 source={require('../assets/card3.png')}/></TouchableOpacity>
			</CardsView>
		</CardView>
		<View>
			<Head>New Card Details</Head>
			<View style={styles.data}>
				<StyledInput placeholder='Card Number'/>
			</View>
			<StyledText>Expiration Date</StyledText>
				<View style={styles.data}>
					<DateBtn><Text>Month</Text></DateBtn>
					<DateBtn><Text>Year</Text></DateBtn>
					<DateView><Text>CVV</Text></DateView>
				</View>
		</View>
		</SafeAreaView>
		)
}

const styles=StyleSheet.create({
	container:{
		backgroundColor:'#000',
		flex:1,
		margin:20
	},
	data:{
		alignItems:'center'
	}

})
