import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity, ImageBackground} from 'react-native';
import Icon2  from 'react-native-vector-icons/AntDesign';
import Icon  from 'react-native-vector-icons/Feather';
import styled from 'styled-components/native';





const Head= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 24px;
letter-spacing: -0.333333px;
color: #C3AD60;
`;


const GymTopImage= styled.ImageBackground`
  
height: 180px;
overflow-x: scroll;

justify-content:flex-end;
background: #FFFFFF;
`;


const HotelName= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 18px;
letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const OtherText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 16px;
letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const AmountView= styled.View`
flex-direction:row;
justify-content:space-around;
align-items:center;
`;


const Amount= styled.View`
width: 72px;
height: 32px;
background: #4E4E4E;
;
border-radius: 15px;
align-items:center;
justify-content:center;
margin:10px;
`;



const HotelImage= styled.Image`
width: 142px;
height: 116px;
border-radius: 5px;
`;

const HotelImages= styled.View`
flex-direction:row;
justify-content:space-around;
flex-wrap:wrap;
`;




export default function Gym({navigation}){
	return(
		<SafeAreaView style={styles.container}>
		<ScrollView>
			<View style={styles.vector}>
	       	 		<TouchableOpacity onPress={()=>navigation.goBack()}>
	          			<Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
	        		</TouchableOpacity>
	        		<View style={{alignItems:'center', justifyContent:'center', width:'100%', right:45}}><Head>Gym and Spa</Head></View>
	        		<Icon name="search" style={{marginRight:25}} size={25} color="#C3AD60" />
	    	</View>
	    	<GymTopImage source={require('../../../assets/hotelpic4.png')} resizeMode="cover">
		 	<View style={{width:'60%'}}>
    				<HotelName>Name of Hotel</HotelName>
    				<OtherText>123, street name, awesome local govt. State.</OtherText>
    		</View>
    		</GymTopImage>
    	<AmountView>
    		<ScrollView horizontal showsVerticalScrollIndicator={false}>
		       <Amount><OtherText>Single</OtherText></Amount>
		       <Amount><OtherText>Double</OtherText></Amount>
		       <Amount><OtherText>Triple</OtherText></Amount>
		       <Amount><OtherText>Quad</OtherText></Amount>
		       <Amount><OtherText>King</OtherText></Amount>
		       <Amount><OtherText>Queen</OtherText></Amount>
		       <Amount><OtherText>Twin</OtherText></Amount>
	       </ScrollView>
     	</AmountView>
     	<HotelImages>
     		<View style={{margin:20}}>
     			<HotelImage source={require('../../../assets/hotelpic1.png')}/>
     			<View style={styles.distance}><OtherText>0.7km away</OtherText></View>
     		</View>
     		<View style={{margin:20}}>
     			<HotelImage source={require('../../../assets/hotelpic1.png')}/>
     			<View style={styles.distance}><OtherText>0.7km away</OtherText></View>
     		</View>
     		<View style={{margin:20}}>
     			<HotelImage source={require('../../../assets/hotelpic1.png')}/>
     			<View style={styles.distance}><OtherText>0.7km away</OtherText></View>
     		</View>
     		<View style={{margin:20}}>
     			<HotelImage source={require('../../../assets/hotelpic1.png')}/>
     			<View style={styles.distance}><OtherText>0.7km away</OtherText></View>
     		</View>
     	</HotelImages>
     	</ScrollView>
    	</SafeAreaView>
		)

}	


const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:'#000'
  },
  vector:{
    flexDirection:'row',
    height:80,
    alignItems:'center',
  },

})
