import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity, ImageBackground} from 'react-native';
import Icon2  from 'react-native-vector-icons/AntDesign';
import styled from 'styled-components/native';




const Head= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 24px;
line-height: 28px;
letter-spacing: -0.333333px;
color: #C3AD60;
`;


const StyledImage= styled.ImageBackground`
width: 360px;
height: 164px;
border-radius: 5px;
align-items:flex-end;
justify-content:flex-end;
`;


const HotelName= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 18px;

letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const OtherText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 16px;

letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const TopTextView= styled.View`
flex-direction:row; 
justify-content:space-between;
`;









export default function Hotel({navigation}){
	const hotelPics=[require('../../../assets/hotelpic1.png'), require('../../../assets/hotelpic2.png'), require('../../../assets/hotelpic3.png')]
	return(
		<SafeAreaView style={styles.container}>
			<View style={styles.vector}>
       	 		<TouchableOpacity onPress={()=>navigation.goBack()}>
          		<Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
        		</TouchableOpacity>
        		<View style={{alignItems:'center', justifyContent:'center', width:'100%', right:45}}><Head>Hotels</Head></View>
    		</View>
    		<ScrollView contentContainerStyle={{flex:1, alignItems:'center', margin:20}} showsVerticalScrollIndicator={false}>	
			{hotelPics.map((item, index) => {
            return (
            <View key={index} style={{margin: 30}}>
             <StyledImage source={item} resizeMode="cover" style={styles.image}>
    			<Icon2 name="hearto" style={{marginRight:10, marginBottom:10}} size={30} color="#fff" />
    		</StyledImage>
    		<TopTextView>
    				<HotelName>Name of Hotel</HotelName><OtherText>0.7km away</OtherText>
    		</TopTextView>
    		<OtherText>Cost per night</OtherText>
    		</View>	
            );
          })}
          

          </ScrollView>
    		

    	</SafeAreaView>	

		)}


const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:'#000'
  },
  vector:{
    flexDirection:'row',
    height:80,
    alignItems:'center',
  },

})
