import React, { useState } from "react";
import Icon from "react-native-vector-icons/AntDesign" ;
import styled from 'styled-components/native';
import { 
    SafeAreaView, 
    View, 
    Text, 
    ScrollView, 
    StyleSheet, 
    TouchableOpacity, 
    TextInput, 
    Pressable, 
    Modal,
    Image 
} from "react-native";
import {  
    FirstView5,
    BigText4,
} from '../../components/screenComponents/fintech5Style';


const Brand= styled.Image`
width: 180px;
height: 180px;
`;

const BrandText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 32px;
color: #FFFFFF;
`;




export default function Vendor1({navigation}){
    return(
        <SafeAreaView style={styles.container}>
            <ScrollView>

                <FirstView5>
                    <TouchableOpacity onPress={()=>navigation.goBack()}>
                        <Icon size={30} color="#C3AD60" name="back"/> 
                    </TouchableOpacity>
                   <BigText4>Product Name</BigText4>
                   <View></View>
                </FirstView5>

                <View style={styles.top}>
                    <Brand source={require('../../../assets/bigBrand.png')}/>
                    <View>
                        <BrandText>N0.00k</BrandText>
                    </View>
                </View>

                
                
            </ScrollView>
        </SafeAreaView>

)}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#000",
        paddingTop:20,
        flex: 1,
    },
    top:{
        flexDirection:'row'
    }
})
