import React from 'react';
import Icon from 'react-native-vector-icons/Feather';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity} from 'react-native';
import {VectorHeader3} from '../../components/screenComponents/components';
import styled from 'styled-components/native';


const StyledText= styled.Text`
  
width: 249px;
height: 76px;

font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 32px;
line-height: 38px;
text-align: center;
letter-spacing: -0.333333px;

color: #FFFFFF;
`;

const StyledVertical= styled.View`
  
width: 75px;
height: 0px;

border: 3px solid #FFFFFF;
transform: rotate(90deg);

`;





export default function TestDrive({navigation}){
	return(
	<SafeAreaView style={styles.container}>
	<ScrollView
	showsVerticalScrollIndicator={false}>
		<VectorHeader3
			goBack={()=>navigation.goBack()}
			handleBrandView={()=>handleBrandView()}/>
		<View>
			<Image source={require('../assets/bigBrand.png')} style={styles.image}/>
		</View>	
		<View style={styles.topText}>
			<StyledText>Product Name N0.00</StyledText>
		</View>
		<View style={styles.deliveryView}><Text>DELIVERY: <Text style={{fontWeight:'bold'}}>N0.00</Text></Text></View>
		<View style={styles.description}><StyledVertical></StyledVertical><Text style={{color:'#fff'}}>Product Description:
This is a more detailed description of product with dimensions and special features/ specifications.</Text></View>
	</ScrollView>	
	</SafeAreaView>		
		)

}
const styles = StyleSheet.create({
	container:{
		flex:1,
		backgroundColor:'#000'
	},
	image:{
		width:"100%",
		height:420
	},
	topText:{
		alignItems:'center',
		justifyContent:'center',
		margin:30
	},
	deliveryView:{
		borderTopRightRadius:10,
		borderBottomRightRadius:10,
		backgroundColor:'#fff',
		width: '38%',
		height: 32,
		paddingRight:8,
		justifyContent:'center',
		alignItems:'flex-end'
	},
	description:{
		flexDirection:'row',
		justifyContent:'space-around',
		alignItems:'center'

	}
})
