import React, { useState } from "react";
import Icon from "react-native-vector-icons/AntDesign" ;
import styled from 'styled-components/native';
import { 
    SafeAreaView, 
    View, 
    Text, 
    ScrollView, 
    StyleSheet, 
    TouchableOpacity, 
    TextInput, 
    Pressable, 
    Modal,
    Image 
} from "react-native";
import {  
    FirstView5,
    BigText4,
} from '../../components/screenComponents/fintech5Style';
import {ww, wp} from '../../../responsive';


const Brand= styled.Image`
width: ${ww(180)}px;
height: ${ww(180)}px;
`;

const BrandText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: ${ww(32)}px;
color: #FFFFFF;
`;

const BrandText2= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(16)}px;
color: #FFFFFF;
`;



const BrandText3= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(10)}px;
color: #4E4E4E;
width: ${ww(152)}px;
`;


const BrandButton= styled.TouchableOpacity`
width: ${ww(145)}px;
height: ${ww(48)}px;
background: #C3AD60;
align-items:center;
justify-content:center;
`;

const BrandButtonTxt= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(24)}px;
text-align: center;
letter-spacing: -0.333333px;
color: #000000;
`;



const BrandText1= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(16)}px;
color: #4E4E4E;
padding-bottom:${ww(3)}px;
`;


const Line= styled.View`
width:${wp(340)}%;
margin: ${ww(50)}px ${ww(20)}px ${ww(50)}px ${ww(20)}px;
height: 0px;
border: 1px solid rgba(250, 246, 243, 0.5);
align-self:center;
`;


const Stat= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(16)}px;
text-align: center;
letter-spacing: -0.408px;

`;


const StatPic= styled.Image`
width: ${wp(360)}%;
height: ${ww(280)}px;
border-radius: 10px;
`;






export default function Vendor1({navigation}){
    const [active, setActive] = useState(1);
    return(
        <SafeAreaView style={styles.container}>
            <ScrollView>

                <FirstView5>
                    <TouchableOpacity onPress={()=>navigation.goBack()}>
                        <Icon size={ww(30)} color="#C3AD60" name="back"/> 
                    </TouchableOpacity>
                   <BigText4>Product Name</BigText4>
                   <View></View>
                </FirstView5>

                <View style={styles.top}>
                    <Brand source={require('../../../assets/bigBrand.png')}/>
                    <View style={styles.brandTxt}>
                        <View>
                            <BrandText1>Price:</BrandText1>
                            <BrandText>N0.00k</BrandText>
                        </View>
                        <View>
                            <BrandText2>Product Description</BrandText2>
                            <BrandText3>Lorem ipsum dolor sit amet Lorem ipsum dolor sit amet Lorem ipsum dolor sit amet </BrandText3>
                        </View>
                        <BrandButton><BrandButtonTxt>Category</BrandButtonTxt></BrandButton>
                    </View>
                </View>

                <Line></Line>

                <View style={styles.stats}>
                    <View style={styles.statText}>
                        <TouchableOpacity onPress={()=>setActive(1)}>
                            <Stat style={active==1 ? {color: '#504219'} : {color: '#C3AD60'}}>Like</Stat>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={()=>setActive(2)}>
                            <Stat style={active==2 ? {color: '#504219'} : {color: '#C3AD60'}}>Like</Stat>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={()=>setActive(3)}>
                            <Stat style={active==3 ? {color: '#504219'} : {color: '#C3AD60'}}>Like</Stat>
                        </TouchableOpacity>
                    </View>
                    <StatPic source={require('../../../assets/stat.png')}/>
                </View>
                
            </ScrollView>
        </SafeAreaView>

)}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#000",
        paddingTop:ww(20),
        flex: 1,
    },
    top:{
        flexDirection:'row',
        justifyContent:'center'
    },
    brandTxt:{
        justifyContent:'space-between'
    },
    stats:{
        alignItems:'center'
    },
    statText:{
        flexDirection:'row',
        justifyContent:'space-between',
        alignItems:'center',
        width:'50%'
    }
})
