import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity, ImageBackground} from 'react-native';
import Icon2  from 'react-native-vector-icons/AntDesign';
import Icon  from 'react-native-vector-icons/Feather';
import styled from 'styled-components/native';



const StyledImage= styled.ImageBackground`
width: 330px;
height: 164px;
border-radius: 5px;
flex-direction:row;
align-items:flex-end;
justify-content:space-between;
`;

const HotelName= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 18px;

letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const OtherText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 16px;

letter-spacing: -0.333333px;

color: #FFFFFF;
`;


export default function Hotel({navigation}){
	return(
	<SafeAreaView style={styles.container}>	
		<View style={styles.vector}>
       	 		<TouchableOpacity onPress={()=>navigation.goBack()}>
          			<Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
        		</TouchableOpacity>
        		<Icon name="search" style={{marginRight:25}} size={25} color="#C3AD60" />
    	</View>
		 <StyledImage source={require('../../../assets/hotelpic2.png')} resizeMode="cover">
		 	<View>
    				<HotelName>Name of Hotel</HotelName>
    				<OtherText>123, street name, awesome local govt. State.</OtherText>
    		</View>
    		<Icon2 name="hearto" style={{marginRight:10, marginBottom:10}} size={30} color="#fff" />
    	</StyledImage>

    </SafeAreaView>	

)}


const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:'#000',
    alignItems:'center'
  },
  vector:{
    flexDirection:'row',
    height:80,
    alignItems:'center',
    justifyContent:'space-between'
  },

})
