import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity} from 'react-native';
import { RadioButton } from 'react-native-paper';
import Icon2  from 'react-native-vector-icons/AntDesign';
import styled from 'styled-components/native';



const TopView= styled.View`
width: 340px;
height: 180px;
justify-content:space-between;
padding:1%;
border: 1px solid #C3AD60;
border-radius: 16px;
margin:15px;
`;

const FrontBack= styled.Image`
width: 26px;
height: 20px;
resize-mode:contain;
`;

const Xbox= styled.Image`
width: 32px;
height: 32px;

`;

const UserName= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 18px;
line-height: 21px;


color: #FFFFFF;
`;

const ThousandText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 32px;
line-height: 38px;


color: #FFFFFF;
`;


const CenterView= styled.View`
		flex-direction: row;
		align-items: center;
		justify-content:space-between;

`;


const TopIcon= styled.Image`
height: 20px;
width: 21.11111068725586px;
`;


const Pic= styled.Image`
width: 54px;
height: 54px;

`;

const PicView= styled.View`
flex-direction:row;
align-items:center;
justify-content:space-between;
margin:15px 0 15px 0;
`;




const SavedBenefitiaries= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 14px;
line-height: 16px;

color: #FFFFFF;

`;


const StyledButton= styled.TouchableOpacity`
  
width: 320px;
height: 48px;
align-items:center;
justify-content:center;
background: #C3AD60;
border-radius: 30px;


`;

const BuyNow= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 24px;
line-height: 28px;
text-align: center;
letter-spacing: -0.333333px;

color: #000000;

`;

const BenefitiariesView= styled.View`

margin:15px;
`;


 


export default function Fintech1({navigation}){
	const topCard = [{color:'#9A8340', topText:'N100,000.00', userName:'User Name'}, {color:'#000', topText:'N100,000.00', userName:'User Name'}, {color:'#9A8340', topText:'N100,000.00', userName:'User Name'}];
	const pics = [require('../../../assets/finpic2.png'), require('../../../assets/finpic3.png'), require('../../../assets/finpic4.png'), require('../../../assets/finpic1.png'), require('../../../assets/finpic5.png')]
	return(
		<SafeAreaView style={styles.container}>
		<View>
		<ScrollView contentContainerStyle={{flexDirection:'row', }} horizontal showsHorizontalScrollIndicator={false}>	
			{topCard.map((item, index) => {
            return (
            
              <TopView style={{backgroundColor:item.color}} key={index}>
				<CenterView>
				<ThousandText>{item.topText}</ThousandText><FrontBack source={require('../../../assets/frontback.png')}/>
				</CenterView>
				<CenterView>
					<CenterView style={{width:'50%'}}><Xbox source={require('../../../assets/delivered.png')}/><UserName>{item.userName}</UserName></CenterView>
					<CenterView style={{width:'20%'}}>
						<TopIcon source={require('../../../assets/phoneBook.png')}/>
						<TopIcon source={require('../../../assets/icon2.png')}/>
					</CenterView>
				</CenterView>
			</TopView>
            );
          })}
          

          </ScrollView>
          </View>
          <BenefitiariesView>
	          <SavedBenefitiaries>Saved Benefitiaries:</SavedBenefitiaries>
	          <PicView>
	          	{pics.map((item, index) => {
					
	            return (
	            
	             <Pic source={item} key={index}/>
	            );
	          })}
	          </PicView>
	      </BenefitiariesView>   
	       <View style={[styles.buyNow, {backgroundColor:'#000'}]}><StyledButton onPress={()=>setIsDisplay(true)}><BuyNow>Continue</BuyNow></StyledButton></View> 
		</SafeAreaView>

	)}

	const styles=StyleSheet.create({
		container:{
			flex:1,
			backgroundColor:'#000',
			padding:20,

		},
		buyNow:{
    alignItems:'center',
    justifyContent:'center',
    margin:60
  },
	})
