import React from 'react';
import Icon from 'react-native-vector-icons/Feather';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity} from 'react-native';
import {VectorHeader3} from '../../components/screenComponents/components';
import styled from 'styled-components/native';
import Icon4  from 'react-native-vector-icons/AntDesign';

const StyledText= styled.Text`
  
width: 249px;
height: 76px;

font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 32px;
line-height: 38px;
text-align: center;
letter-spacing: -0.333333px;

color: #FFFFFF;
`;

const StyledVertical= styled.View`
  
width: 75px;
height: 0px;
background-color:#fff;
border: 1.5px solid #FFFFFF;
transform: rotate(90deg);

`;
const ProductDescription= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 18px;
line-height: 21px;
letter-spacing: -0.333333px;

color: #FFFFFF;


`;

const Description= styled.Text`
  
font-family: Roboto;
font-size: 18px;
font-weight: 300;
line-height: 21px;
letter-spacing: -0.3333333432674408px;
text-align: left;

color: #FFFFFF;


`;

const StyledButton= styled.TouchableOpacity`
  
width: 320px;
height: 48px;
align-items:center;
justify-content:center;
background: #C3AD60;
border-radius: 30px;


`;

const StyledButton2= styled.TouchableOpacity`
  
width: 320px;
height: 48px;
align-items:center;
justify-content:center;
border-radius: 30px;


`;


const BuyNow= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 24px;
line-height: 28px;
text-align: center;
letter-spacing: -0.333333px;

color: #000000;

`;









export default function TestDrive({navigation}){
	return(
	<SafeAreaView style={styles.container}>
	<ScrollView
	showsVerticalScrollIndicator={false}>
		<VectorHeader3
			goBack={()=>navigation.goBack()}
			handleBrandView={()=>handleBrandView()}/>
		<View>
			<Image source={require('../assets/bigBrand.png')} style={styles.image}/>
		</View>	
		<View style={styles.topText}>
			<StyledText>Product Name N0.00</StyledText>
		</View>
		<View style={styles.deliveryView}><Text>DELIVERY: <Text style={{fontWeight:'bold'}}>N0.00</Text></Text></View>
		<View style={styles.description}><StyledVertical></StyledVertical><Text style={{flexDirection:'column'}}><ProductDescription>Product Description:</ProductDescription>
	<Description>This is a more detailed description of product with dimensions and special features/ specifications.</Description></Text></View>
	<View style={{alignItems:'flex-end', marginRight:20}}><View style={styles.message}><Icon4 name="message1" size={25} color="#000" /></View></View>
	<View style={styles.buyNow}><StyledButton><BuyNow>Buy Now</BuyNow></StyledButton></View>
	<View style={styles.buyNow}><StyledButton2><BuyNow>Add to Cart</BuyNow></StyledButton2></View>
	</ScrollView>	
	</SafeAreaView>		
		)

}
const styles = StyleSheet.create({
	container:{
		flex:1,
		backgroundColor:'#000'
	},
	image:{
		width:"100%",
		height:420
	},
	topText:{
		alignItems:'center',
		justifyContent:'center',
		margin:30
	},
	deliveryView:{
		borderTopRightRadius:10,
		borderBottomRightRadius:10,
		backgroundColor:'#fff',
		width: '38%',
		height: 32,
		paddingRight:8,
		justifyContent:'center',
		alignItems:'flex-end'
	},
	description:{
		flexDirection:'row',
		justifyContent:'space-around',
		alignItems:'center',
		marginTop:25,
		marginLeft:20,
		marginRight:20,

	},
	message:{
		borderRadius:50,
		width:48,
		height:48,
		backgroundColor:'#C3AD60',
		alignItems:'center',
		justifyContent:'center',
		bottom:15
	},
	buyNow:{
		alignItems:'center',
		justifyContent:'center',
		marginTop:20,
		marginBottom:20
	}
})
