import React, { useState, useEffect } from "react";
import Icon from 'react-native-vector-icons/Feather';
import Icon2  from 'react-native-vector-icons/AntDesign';
import { SafeAreaView, View, Text, ScrollView, StyleSheet, TouchableOpacity, TextInput, ImageBackground, Dimensions } from "react-native";
import styled from 'styled-components/native';

/*
${wp()}%;
${hh()}px;
${ww()}px;
*/


const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const ww = (a)=>windowWidth*a/428;
const hh = (a)=>windowHeight*a/1024;
const wp = (a)=>a/428*100;

export const SearchBar = styled.View`
width: ${wp(360)}%;
height: ${hh(36)}px;
flex-direction:row;
align-items:center;
background: #4E4E4E;
border-radius: 30px;
`;


export const TopText = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: ${ww(12)}px;
color: #FFFFFF;
`;


export const BottomText1 = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 700;
font-size: ${ww(24)}px;
color: #FFFFFF;
`;


export const BottomText2 = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: ${ww(12)}px;
color: #C3AD60;
`;


export const TopTrend = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 700;
font-size: ${ww(18)}px;
color: #C3AD60;
`;


export const Button1 = styled.TouchableOpacity`
width: ${ww(116)}px;
height: ${hh(28)}px;
border: 1px solid #4E4E4E;
border-radius: 15px;
`;


export const ButtonText1 = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: ${ww(12)}px;
text-align: center;
color: #4E4E4E;
`;







export default function Search({navigation}) {
    
        return(
        <SafeAreaView style={styles.container}>
            <View style={{alignItems:'center'}}>
                <SearchBar>
                    <Icon2 size={ww(20)} color="#C3AD60" name="back"/>
                    <TextInput style={styles.search}/>
                    <Icon name="search" size={ww(20)} color="#C3AD60" />
                </SearchBar>
            </View>

            <ImageBackground source={require('../../../assets/searchImage.png')} style={styles.image}>
                <View style={styles.imageTexts}>
                    <TopText>Trend Industry</TopText>
                    <View style={styles.bottom}>
                        <BottomText1>Trending Topics</BottomText1>
                        <BottomText2>Top Trend</BottomText2>
                    </View>
                </View>    
            </ImageBackground>

            <View>
                <View>
                    <TopTrend>Top Trends In Your Location</TopTrend>
                    <Button1><ButtonText1>Change Location</ButtonText1></Button1>
                </View>
            </View>
        </SafeAreaView>
        )
    
    
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#000",
        paddingTop:20,
        flex:1
    },
    search:{
        width:'100%'
    },
    image:{
        height:256, 
        marginTop:20,
        justifyContent:'flex-end'
    },
    bottom:{
        flexDirection:'row',
        justifyContent:'space-between',
        alignItems:'center',
        width:'100%',
        
    },
    imageTexts:{
        margin:20
    },

})
