import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity, ImageBackground} from 'react-native';
import Icon2  from 'react-native-vector-icons/AntDesign';
import Icon  from 'react-native-vector-icons/Feather';
import styled from 'styled-components/native';



const StyledImage= styled.ImageBackground`
width: 360px;
height: 360px;
overflow-x: scroll;
flex-direction:row;
align-items:flex-end;
justify-content:space-between;
align-self:center;
`;

const HotelName= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 18px;

letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const OtherText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 16px;
letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const Service= styled.View`
  
width: 96px;
height: 96px;
background: #4E4E4E;
border-radius: 5px;
align-items:center;
justify-content:center;
`;


const Amount= styled.View`
width: 72px;
height: 32px;
background: #C3AD60;
border-radius: 15px;
align-items:center;
justify-content:center;
margin:10px;
`;


const AmountView= styled.View`
flex-direction:row;
flex-wrap:wrap;
justify-content:space-around;
align-items:center;
`;



const BottomView= styled.View`
height: 96px;
background: #9A8340;
flex-direction:row;
align-items:center;
justify-content:space-between;
`;



const BottomText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 24px;

color: #FFFFFF;
`;


const BottomOtherText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 24px;

color: #FFFFFF;
`;


const BottomButton= styled.TouchableOpacity`
  
width: 153px;
height: 59px;
background: #000000;
border-radius: 30px;
align-items:center;
justify-content:center;
`;



export default function Hotel({navigation}){
	return(
	<SafeAreaView style={styles.container}>	
    <ScrollView>
		<View style={styles.vector}>
       	 		<TouchableOpacity onPress={()=>navigation.goBack()}>
          			<Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
        		</TouchableOpacity>
        		<Icon name="search" style={{marginRight:25}} size={25} color="#C3AD60" />
    	</View>
		 <StyledImage source={require('../../../assets/hotelpic4.png')} resizeMode="cover">
		 	<View style={{width:'60%'}}>
    				<HotelName>Name of Hotel</HotelName>
    				<OtherText>123, street name, awesome local govt. State.</OtherText>
    		</View>
    		<Icon2 name="hearto" style={{marginRight:10, marginBottom:10}} size={30} color="#fff" />
    	</StyledImage>
     <View style={styles.distance}><OtherText>0.7km away</OtherText></View> 
     <View style={styles.middle}>
        <OtherText>0.7km away</OtherText>
        <View style={styles.services}>
          <Service>
            
            <OtherText>WiFi</OtherText>
          </Service>
          <Service>

            <OtherText>Parking</OtherText>
          </Service>
          <Service>
            
            <OtherText>Food</OtherText>
          </Service>
       </View>
     </View>
     <View style={{alignItems:'center', justifyContent:'center'}}>
     <AmountView>
       <Amount><OtherText>Single</OtherText></Amount>
       <Amount><OtherText>Double</OtherText></Amount>
       <Amount><OtherText>Triple</OtherText></Amount>
       <Amount><OtherText>Quad</OtherText></Amount>
       <Amount><OtherText>King</OtherText></Amount>
       <Amount><OtherText>Queen</OtherText></Amount>
       <Amount><OtherText>Twin</OtherText></Amount>
     </AmountView>
     </View>
     </ScrollView>
     <BottomView>
        <View style={{flexDirection:'row', alignItems:'center', width:'40%'}}>
          <BottomText>N0.0</BottomText>
          <OtherText>/Night</OtherText>
        </View>
        <BottomButton><BottomOtherText>Book Now</BottomOtherText></BottomButton>
     </BottomView>
    </SafeAreaView>	

)}


const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:'#000',
  },
  vector:{
    flexDirection:'row',
    height:80,
    alignItems:'center',
    justifyContent:'space-between',
    width:'100%'
  },
  distance:{
    flexDirection:'row', 
    alignItems:'flex-end', 
    justifyContent:'flex-end',
     marginRight:20
  },
  middle:{
    margin:20,
    marginTop:40
  },
  services:{
    flexDirection:'row',
    alignItems:'center',
    justifyContent:'space-between'
  }

})
