import React, { useState } from "react";
import Icon from "react-native-vector-icons/AntDesign";
import Icon2 from "react-native-vector-icons/MaterialIcons"; 
import Icon3 from "react-native-vector-icons/EvilIcons";
import { launchCamera, launchImageLibrary } from "react-native-image-picker";
import { SafeAreaView, View, Text, ScrollView, StyleSheet, TouchableOpacity, TextInput, Pressable } from "react-native";
import {  
   LastView,
   PriceColumn,
   Total,
   Pricing,
   Paytxt,
   Second,
   SelectTxt,
   Radiobtn,
   Quantity1,
   Third
} from "../componenets/fintech10Style"
import { BigText5, FirstView6,  } from "../componenets/fintech6Style";

import {PhotoView1, QtyText1,} from "../componenets/fintech5Style"


export default function Notifications({navigation, route}){
    const [routee, setRoute]= useState(route.params.paramKey);
    return(
        <SafeAreaView style={styles.notificationsContainer}>
            <ScrollView>
            <FirstView6>
            <TouchableOpacity onPress={()=>navigation.goBack()}>
                <Icon size={30} color="#C3AD60" name="back" style={styles.pad}/>
            </TouchableOpacity>
            <BigText5 style={styles.padd}>Notifications</BigText5>
            <TouchableOpacity onPress={()=>navigation.navigate("Fintech11")}>
                <Icon2 size={20} color="#C3AD60" name='history'/>
            </TouchableOpacity>
            </FirstView6>
            <Second>
                <SelectTxt>Select All</SelectTxt>
                <Radiobtn></Radiobtn>
            </Second>
            <Third>
            <Pressable>
                <PhotoView1 source={require('../../../../assets/pro.png')} >
                    <Quantity1><QtyText1>Qty: 1</QtyText1><Icon name="caretdown" size={6} color="#fff"/></Quantity1>
                    <View>
                        <Icon3 name="trash" size={20} color="#fff"/>
                    </View>
                    <Radiobtn></Radiobtn>
                    <View style={styles.linkView}>
                    <Text 
                    style={styles.link}
                    >
                        {routee}
                    </Text>
                    </View>
                   
                </PhotoView1>
                </Pressable>
            </Third>
            </ScrollView>
            <LastView>
                <PriceColumn>
                    <Total>Total:</Total>
                    <Pricing>N0.00</Pricing>
                </PriceColumn>
                <TouchableOpacity style={styles.pay}>
                    <Paytxt>Pay</Paytxt>
                </TouchableOpacity>
            </LastView>
        </SafeAreaView>

)}

const styles = StyleSheet.create({
    notificationsContainer: {
        backgroundColor: "#000",
        paddingTop: 25,
        flex: 1
    },
    defaulttext: {
        color: "#fff",
    },
    pad: {
        paddingLeft: 30,
    },
    padd: {
        paddingLeft: 80,
        paddingRight: 80,
    },
    pay: {
        width: 180,
        height: 48,
        borderRadius: 100,
        backgroundColor: "#C3AD60",
        alignItems: "center",
        justifyContent: "center"
    },
    linkView: {
        height: 54,
        width: 283,
        backgroundColor: "#4E4E4E",
        borderBottomLeftRadius: 13,
        borderBottomRightRadius: 13,
        paddingLeft: 25,
        justifyContent:"center",
        marginTop: 82
        
    },
    link: {
        color:"#fff",
    }

})
