import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity, ImageBackground} from 'react-native';
import Icon2  from 'react-native-vector-icons/AntDesign';
import Icon  from 'react-native-vector-icons/Feather';
import Icon3  from 'react-native-vector-icons/Entypo';
import styled from 'styled-components/native';



const StyledImage= styled.Image`
width: 56px;
height: 56px;
border-radius: 5px;
  
`;


const StyledText1= styled.Text`
font-family: Roboto;
font-size: 16px;
font-weight: 300;
line-height: 19px;
color: #C4C4C4;  
`;


const StyledText2= styled.Text`
font-family: Roboto;
font-size: 24px;
font-weight: 700;
line-height: 28px;
 color: #FFFFFF; 
`;

const StyledText3= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 18px;

color: #FFFFFF;
`;








export default function Category1({navigation}){

	return(
	<SafeAreaView style={styles.container}>	
        <View style={styles.row}>
            <View style={[styles.row, {height:'20%'}]}>
                <StyledImage source={require('../../../assets/foodImg2.png')}/>
                <View>
                    <StyledText2>Name of meal</StyledText2>
                    <StyledText1>Detailed description of meal</StyledText1>
                    <StyledText3>N0.00</StyledText3>
                </View>
            </View>
            <View style={styles.right}>
                <Icon name="clock" size={16} color="#C3AD60" />
                <StyledText1>30 mins</StyledText1>
            </View>
        </View>
    </SafeAreaView>	

)}


const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:'#000',
  },
  row:{
    flexDirection:'row',
    justifyContent:'space-between'
  },
  right:{
    width:20,
    justifyContent:'space-between'
  },


})
