import styled from "styled-components/native";
import { Constants } from "../../../../constants/db.mock";

// PAYMENT SCREEN STYLES START
export const PaymentContainer = styled.View`
  flex: 1;
  background-color: ${Constants.theme.dark};
  padding-right: 35px;
  padding-left: 35px;
`;

export const Section = styled.View`
  margin: 30px 0;
`;

export const Title = styled.Text`
  color: ${Constants.theme.primary};
  margin-top: 20px;
  margin-bottom: 20px;
  font-size: 20;
`;
export const SubTitle = styled.Text`
  color: #fff;
  margin-bottom: 30px;
  margin-top: 30px;
  font-size: 15px;
`;
export const CardInfoContainer = styled.View`
  flex-direction: row;
  justify-content: space-between;
`;

export const CardDateContainer = styled.View`
  flex-direction: row;
  justify-content: space-between;
  width: 60%;
  align-items: center;
`;

export const CardDateDevider = styled.Text`
  color: #fff;
  font-size: 20;
`;
// PAYMENT SCREEN STYLES END

// PAYMENT OPTIONS STYLES START
