import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity} from 'react-native';
import {products} from './list';
import styled from 'styled-components/native';
import {ww, wp} from '../../../responsive';



const TopText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: ${ww(18)}px;
margin-bottom: ${ww(10)}px;
margin-left: ${ww(15)}px;
letter-spacing: -0.333333px;
color: #FFFFFF;
`;



const BottomText= styled.Text`
width: ${wp(328)}%;

font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(14)}px;
margin-left: ${ww(15)}px;
letter-spacing: -0.333333px;
color: #FFFFFF;
`;


const CardView= styled.View`
border-bottom-color:#C3AD60;
border-bottom-width:0.2px;
margin:${ww(10)}px ${ww(20)}px ${ww(10)}px ${ww(20)}px;
padding-bottom:${ww(20)}px;
`;



const BottomView= styled.View`
border-top-color:#C3AD60;
border-top-width:0.2px;
padding:${ww(10)}px ${ww(20)}px ${ww(10)}px ${ww(20)}px;
`;



const LicenseView= styled.View`
width: 100%;
height: ${ww(85)}px;
background: #181818;
border-radius: 15px;
flex-direction:row;
margin:${ww(5)}px 0 ${ww(5)}px 0;
`;



const License= styled.Image`
width: ${ww(90.35)}px;
height: ${ww(64)}px;
border-radius: 10px;
`;



const LicenseTopText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 700;
font-size: ${ww(14)}px;
letter-spacing: -0.333333px;
margin: ${ww(10)}px 0 0 ${ww(25)}px;
color: #E6E6E6;
`;



const LicenseBottomText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(12)}px;
letter-spacing: -0.333333px;

color: #E6E6E6;
`;



const LicenseButton= styled.TouchableOpacity`
width: ${ww(240)}px;
height: 24px;
background: #C3AD60;
opacity: 0.9;
border-radius: 15px;
align-items:center;
justify-content:center;
`;



const LicenseButtonText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 10px;
letter-spacing: -0.333333px;

color: #000000;
`;



const BottomImg= styled.Image`
width: ${ww(50)}px;
height: ${ww(50)}px;
border-radius: 5px;
margin:0 ${ww(20)}px 0 0;
`;




const BrandName= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 700;
font-size: 18px;
letter-spacing: -0.333333px;

color: #FFFFFF;
`;




const BrandDescription= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 14px;
letter-spacing: -0.333333px;
color: #FFFFFF;
`;



const Link= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 15px;
text-align: center;
letter-spacing: -0.333333px;
text-decoration-line: underline;

color: #458DF9;
`;



const UploadButton= styled.TouchableOpacity`
width: 150px;
height: 30px;
background: #C3AD60;
border-radius: 15px;
align-items:center;
justify-content:center;
`;



const UploadText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 12px;
text-align: center;
letter-spacing: -0.333333px;

color: #000000;
`;



const ServiceCard= styled.View`
	border-radius:20px;
	height: ${ww(128)}px;
	background: #181818;
	width: ${ww(360)}px;
	flexDirection:row;
	margin: ${ww(15)}px;
`;



const ServiceCardImage= styled.Image`
	width: ${ww(149.27)}px;
	height: ${ww(128)}px;
	border-top-left-radius:20px;
	border-bottom-left-radius:20px;
`;



const ServiceName= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 700;
font-size: 14px;
line-height: 16px;
letter-spacing: -0.333333px;
text-align:center;
color: #FFFFFF;
`;




const ServiceDescription= styled.Text`
width: 178.9px;
height: 14px;
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 12px;
letter-spacing: -0.333333px;
text-align:center;
color: #FFFFFF;
`;




const StatisticButton= styled.TouchableOpacity`
width: 131.71px;
height: 30px;
background: #C3AD60;
border-radius: 15px;
align-items:center;
justify-content:center;
`;








const StyledImage= styled.Image`
width: 42px;
height: 42px;
border-radius:50px;
margin: 0 20px 0 0;
`;

const PostText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(18)}px;
width: ${ww(300)}px;
color: #FFFFFF;
padding:0;
margin:0;
`;

const Handle= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: 14px;
color: #FFFFFF;
opacity: 0.5;
