import React from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity} from 'react-native';
import { RadioButton } from 'react-native-paper';
import Icon2  from 'react-native-vector-icons/AntDesign';
import styled from 'styled-components/native';


const StyledCard1= styled.Image`
width: 85.33px;
height: 48px;
border: 1px solid #000000;
filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
`;

const CardsView= styled.View`
flex-direction: row;
align-items: center;
justify-content: space-around;
`;

const CardView= styled.View`
margin: 20px 0 20px 0;
`;

const Head= styled.Text`

font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 20px;
margin: 20px 0 20px 0;
color: #C3AD60;

border: 1px solid #000000;
text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;

const StyledInput= styled.TextInput`
height: 48px;
width:100%;
background: #4E4E4E;
border-radius: 5px;
margin: 20px 0 20px 0;
`;



const StyledText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 14px;
color: #FFFFFF;
margin: 20px 0 20px 0;
`; 

const DateBtn= styled.TouchableOpacity`
width: 96px;
height: 48px;
align-items:center;
justify-content:center;
background-color: #C3AD60;
border-radius: 10px;
margin-right:5px;
`;

const StyledDateView= styled.TextInput`
width: 96px;
height: 48px;
background: #4E4E4E;
border-radius: 5px;
margin-Right:5px;
`;

const StyledButton= styled.TouchableOpacity`
  
width: 320px;
height: 48px;
align-items:center;
justify-content:center;
background: #C3AD60;
border-radius: 30px;


`;

const BuyNow= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 24px;
line-height: 28px;
text-align: center;
letter-spacing: -0.333333px;

color: #000000;

`;




export default function Ecommerce8({navigation}){
	return(
		<SafeAreaView style={styles.container}>
			<ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
			<View style={styles.vector}>
		        <TouchableOpacity onPress={()=>navigation.goBack()}>
		          <Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
		        </TouchableOpacity>
   			 </View>
				<CardView>
					<Head>Saved Cards</Head>
					<CardsView>
						<TouchableOpacity><StyledCard1 source={require('../assets/card1.png')}/></TouchableOpacity>
						<TouchableOpacity><StyledCard1 source={require('../assets/card2.png')}/></TouchableOpacity>
						<TouchableOpacity><StyledCard1 source={require('../assets/card3.png')}/></TouchableOpacity>
					</CardsView>
				</CardView>
				<View>
					<Head>New Card Details</Head>
					<View style={styles.data}>
						<StyledInput placeholder='Card Number'/>
					</View>
					<StyledText>Expiration Date</StyledText>
						<View style={styles.data2}>
							<View style={styles.data2}>
								<DateBtn><Text>Month</Text></DateBtn>
								<DateBtn><Text>Year</Text></DateBtn>
							</View>	
							<StyledDateView placeholder='CVV'/>
						</View>
					<View style={styles.buyNow}><StyledButton onPress={()=>navigation.navigate('Ecommerce8')}><BuyNow>Continue</BuyNow></StyledButton></View>	
				</View>
			</ScrollView>	
		</SafeAreaView>
		)
}

const styles=StyleSheet.create({
	container:{
		backgroundColor:'#000',
		flex:1,
	},
	scrollView:{
		backgroundColor:'#000',
		flex:1,
		margin:20
	},
	vector:{
    flexDirection:'row',
    height:80,
    alignItems:'center',
  },
	data:{
		alignItems:'center'
	},
	data2:{
		flexDirection:'row',
		alignItems:'center',
		justifyContent:'space-between'
	},
	buyNow:{
    alignItems:'center',
    justifyContent:'center',
    margin:60
  },

})
