import React, { useState } from "react";
import Icon from 'react-native-vector-icons/Feather';
import Icon2  from 'react-native-vector-icons/AntDesign';
import { SafeAreaView, View, Text, ScrollView, StyleSheet, TouchableOpacity, TextInput, ImageBackground } from "react-native";
import styled from 'styled-components/native';



export const SearchBar = styled.View`
width: 360px;
height: 36px;
flex-direction:row;
align-items:center;
background: #4E4E4E;
border-radius: 30px;
`;


export const TopText = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: 12px;
color: #FFFFFF;
`;


export const BottomText1 = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 700;
font-size: 24px;
color: #FFFFFF;
`;


export const BottomText2 = styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 400;
font-size: 12px;
color: #C3AD60;
`;






export default function Search({navigation}) {
    return(
        <SafeAreaView style={styles.container}>
            <View style={{alignItems:'center'}}>
                <SearchBar>
                    <Icon2 size={30} color="#C3AD60" name="back"/>
                    <TextInput style={styles.search}/>
                    <Icon name="search" size={25} color="#C3AD60" />
                </SearchBar>
            </View>
            <ImageBackground source={require('../../../assets/searchImage.png')} style={styles.image}>
                <TopText>Trend Industry</TopText>
                <View style={styles.bottom}>
                    <BottomText1>Trending Topics</BottomText1>
                    <BottomText2>Top Trend</BottomText2>
                </View>
            </ImageBackground>
        </SafeAreaView>
        )
    
    
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#000",
        paddingTop:20,
        flex:1
    },
    search:{
        width:'100%'
    },
    image:{
        height:256, 
        marginTop:20
    },
    bottom:{
        flexDirection:'row',
        justifyContent:'space-between',
        width:'100%'
    }

})
