import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity} from 'react-native';
import {products} from './list';
import styled from 'styled-components/native';
import {ww, wp} from '../../../responsive';



const TopText= styled.Text`
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: ${ww(18)}px;
margin-bottom: ${ww(10)}px;
letter-spacing: -0.333333px;
color: #FFFFFF;
`;



const BottomText= styled.Text`
width: ${wp(328)}%;

font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: ${ww(14)}px;
letter-spacing: -0.333333px;
color: #FFFFFF;
`;


const Vieww= styled.View`
border-bottom-color:#C3AD60;
border-bottom-width:0.2px;
margin:${ww(10)}px ${ww(20)}px ${ww(10)}px ${ww(20)}px;
padding-bottom:${ww(20)}px;
`;



const LicenseView= styled.View`
width: ${ww(360)}px;
height: ${ww(85)}px;
background: #181818;
border-radius: 15px;
flex-direction:row;
align-items:center;
`;



const License= styled.Image`
width: ${ww(90.35)}px;
height: ${ww(64)}px;
border-radius: 10px;
`;





export const Products=({navigation, route})=>{
	return(
		<View style={styles.products}>
				<FlatList
						data={products}
						numColumns={3}
						showsVerticalScrollIndicator={false}
						keyExtractor={(item, index)=>index.toString()}
						renderItem={({item, index})=>
							<View style={styles.imageVieww}>
							<TouchableOpacity onPress={()=>navigation.navigate('Ecommerce5', {image:item.image})} style={styles.imageView}>
								<Image source={item.image} style={styles.image} key={index}/>
								<Text style={styles.brandName}>Product Name</Text>
								<Text style={styles.km}>N0.00</Text>
							</TouchableOpacity>	
							</View>
						}
						/>

			</View>
		)
}

export const About=()=>{
	return(
			<ScrollView style={{backgroundColor:'#000000', flex:1}}>
				<Vieww>
					<TopText>Brand Description</TopText>
					<BottomText>
						Lorem ipsum dolor sit amet, consectetur adipiscing elit. Dapibus a auctor sit sed auctor. 
						Faucibus odio maecenas facilisi orci. Aliquet porttitor in malesuada turpis tortor vitae ut volutpat. 
						Viverra tortor dignissim aliquam et augue eu et sed sociis.
					</BottomText>
				</Vieww>

				<Vieww>
					<TopText>Location</TopText>
					<BottomText>
						Lorem ipsum dolor sit amet, consectetur adipiscing
					</BottomText>
				</Vieww>

				<Vieww>
					<TopText>Work History</TopText>
					<BottomText>
						Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
						Euismod pellentesque vitae, id nibh id sem feugiat malesuada id. Facilisi sed lacus, amet.
					</BottomText>
				</Vieww>

				<Vieww>
					<TopText>Skills</TopText>
					<LicenseView>
						<License source={require('../../../assets/license1.png')}/>
					</LicenseView>
				</Vieww>


			</ScrollView>
		)
}



export const Reviews=()=>{
	return(
		<View style={{alignItems:'center', justifyContent:'center', flex:1, backgroundColor:'#000000'}}>
			</View>
		)
}





const styles = StyleSheet.create({
	
	brandName:{
		fontSize:17, 
		fontWeight:'400', 
		fontFamily:'Roboto', 
		color:'#fff', 
		fontSize:10,
		textAlign:'center',
	},
	km:{
		color:'#4E4E4E', 
		fontFamily:'Roboto',
		fontSize:10,
		textAlign:'center',

	},
	
	image:{
		height:60,
		width:60,
		margin:20,
		borderRadius:17
	},
	
	imageVieww:{
		alignItems:'center',
		justifyContent:'center',
		margin:10,
		padding:0
	},
	
	products:{
		flex:1, 
		backgroundColor:'#000000', 
		justifyContent:'center',
		alignItems:'center'
	},
	



})
