import React, {useState} from 'react';
import {View, SafeAreaView, Text, StyleSheet, TextInput, FlatList, Image, ScrollView, TouchableOpacity, ImageBackground} from 'react-native';
import Icon2  from 'react-native-vector-icons/AntDesign';
import Icon  from 'react-native-vector-icons/Feather';
import styled from 'styled-components/native';



const StyledImage= styled.ImageBackground`
width: 360px;
height: 360px;
overflow-x: scroll;
flex-direction:row;
align-items:flex-end;
justify-content:space-between;
align-self:center;
`;

const HotelName= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 900;
font-size: 18px;

letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const OtherText= styled.Text`
  
font-family: 'Roboto';
font-style: normal;
font-weight: 300;
font-size: 16px;
letter-spacing: -0.333333px;

color: #FFFFFF;
`;


const Service= styled.View`
  
width: 96px;
height: 96px;
background: #4E4E4E;
border-radius: 5px;
`;



export default function Hotel({navigation}){
	return(
	<SafeAreaView style={styles.container}>	
    <ScrollView>
		<View style={styles.vector}>
       	 		<TouchableOpacity onPress={()=>navigation.goBack()}>
          			<Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
        		</TouchableOpacity>
        		<Icon name="search" style={{marginRight:25}} size={25} color="#C3AD60" />
    	</View>
		 <StyledImage source={require('../../../assets/hotelpic4.png')} resizeMode="cover">
		 	<View style={{width:'60%'}}>
    				<HotelName>Name of Hotel</HotelName>
    				<OtherText>123, street name, awesome local govt. State.</OtherText>
    		</View>
    		<Icon2 name="hearto" style={{marginRight:10, marginBottom:10}} size={30} color="#fff" />
    	</StyledImage>
     <View style={styles.distance}><OtherText>0.7km away</OtherText></View> 
     <View style={styles.middle}>
        <OtherText>0.7km away</OtherText>
        <View style={styles.services}>
          <Service>
            <Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
            <OtherText>0.7km away</OtherText>
          </Service>
          <Service><Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
            <OtherText>0.7km away</OtherText>
          </Service>
          <Service>
            <Icon2 name="back" style={{marginLeft:20}} size={25} color="#C3AD60" />
            <OtherText>0.7km away</OtherText>
          </Service>
       </View>
     </View>
     </ScrollView>
    </SafeAreaView>	

)}


const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:'#000',
  },
  vector:{
    flexDirection:'row',
    height:80,
    alignItems:'center',
    justifyContent:'space-between',
    width:'100%'
  },
  distance:{
    flexDirection:'row', 
    alignItems:'flex-end', 
    justifyContent:'flex-end',
     marginRight:20
  },
  middle:{
    marginTop:40
  },
  services:{
    flexDirection:'row',
    alignItems:'center',
    justifyContent:'space-between'
  }

})
